// stdio.h is the header file of the program
#include <stdio.h>

// Gerneral syntax of the C program

// int is the return type
int main(){
    // Declaration with definition
    int var1;
    int var2;
    // Declaration with initialization
    var1 = 12;
    var2 = 8;

    // Have to use format specifier inside print statements for numbers
    printf("The sum of the 2 variables is %d\n", (var1+var2));
    
    
    printf("Hello World!!");

    return 0;
}
